import styled from "styled-components";


function PlaylistEntry(props){


    const artistLabels = props.artists.map((artist, i)=>{
        return <a href = {artist.artistUrl} target = "_blank" >{artist.artistName}</a>
    });

    return (
        <Parent>
            <ArtistLablesParent>
                {artistLabels}
            </ArtistLablesParent>
            <PlaylistImg src = {props.imageUrl}/>
        </Parent>

    )
}

const Parent = styled.div`
position: relative;
margin: 1rem;
`

const ArtistLablesParent = styled.div`
position: absolute;
max-width: 100%;
display: flex;
left: 0;
top: 0;
flex-wrap: wrap;
opacity: 1;
transition: 0.3s;
`

const PlaylistImg = styled.img`
max-width: 100%;
align-self: center;
`

const Li = styled.li`
    color: blue;
    background: ${props=>props.color || "red"};
`

export default PlaylistEntry;
