import { useRef, useState } from "react";
import styled from "styled-components";

const axios = require('axios');

export const YoutubeDL = () => {

    let [text, setText] = useState();
    let [error, setError] = useState(null);
    let [convertFormat, setConvertFormat] = useState("mp3");

    const convertYT = async (event)=>{
        try{
            const response = await fetch('http://localhost:3001/test', 
            {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json",
                },

                mode:'cors',
                body: JSON.stringify({
                    url: text
                })
            });
            const data = await response.json();
        }
        catch(error){
            setError(error);
        };

    }

    const formatButtons = ["mp3, mp4"].map(format=>{
        return <FormatButton 
        onClick = {()=>{setConvertFormat(format)}}
        format = {format}
        active= {convertFormat == format}
        />
    })
    return(

        <YoutubeDLContainer>
            <h1>youtube dl</h1>
            <input onChange={
                (e)=>{
                    setText(e.target.value);
                }
            } type="text" />
            <FormatContainer>
                {formatButtons}
            </FormatContainer>
            <button onClick={convertYT}>convert yt</button>
            {error && (<p>{error.toString()}</p>)}
        </YoutubeDLContainer>


    )
}

function FormatButton({onClick, format, active}){
    return (
        <StyledFormatButton
            $active = {active}
            onClick = {onClick}
        >{format}</StyledFormatButton>
    )
}

const YoutubeDLContainer = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    input {

    }
`
const FormatContainer = styled.div`

    display: flex;
    justify-content: center;

`

const StyledFormatButton = styled.button`
    background-color: ${props => props.$active?"red":"white"};

`
export default YoutubeDL;
