import { useEffect, useState } from "react";
import styled from "styled-components";

const buttonLabels = ["one", "two", "three"];
let x = 0;
function ButtonList (){

    const [currentButton, setCurrentButton] = useState("one");

    const onButtonClick = (label)=>{
        setCurrentButton(label);
    }
    const buttons = buttonLabels.map((label, i) =>{
        return (
            <Button
                key = {i}
                $active = {currentButton == label}
                onClick = { ()=> onButtonClick(label)}
            >
                {label}
            </Button>
        )
    });


    return(
        <Wrapper>
            {buttons}
        </Wrapper>
    )
}


const Wrapper = styled.div`
    display: inline-flex;
`
const Button = styled.button`
    display: flex;
    background: ${props => props.$active?"red":"white"};
    border: 2px solid black;
    padding: 5px 30px;
    &:hover {
        background: green;
    }
`
export default ButtonList;
