import { useRef, useState } from "react";
import styled from "styled-components";

const axios = require('axios');

export const YoutubeDL = () => {

    const [error, setError] = useState(null);
    const [convertFormat, setConvertFormat] = useState("m4a");
    const [downloadReady, setDownloadReady] = useState(false);
    const textInput = useRef(null);

    const convertYT = async ()=>{
        const text = textInput.current.value;
        try{
            const response = await fetch('http://localhost:3001/convertYoutube', 
            {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json",
                },

                mode:'cors',
                body: JSON.stringify({
                    url: text,
                    format: convertFormat
                })
            });
            // const data = await response.json();
            console.log(response);
            if(response.ok){
                console.log("success!!!");
                setDownloadReady(true);
            }else{
                setError(response.error);
            }

        }
        catch(error){
            setError(error);
        };

    }

    const formatButtons = ["m4a", "mp4"].map(format=>{
        return <FormatButton 
        onClick = {()=>{setConvertFormat(format)}}
        format = {format}
        key = {format}
        active= {convertFormat == format}
        />
    })
    const downloadVideo = async ()=>{
        try{
            const response = await fetch('http://localhost:3001/download', 
            {
                method: 'GET',
                headers: {
                    "Content-Type": "application/json",
                },

                mode:'cors',
            });
            // const data = await response.json();
            console.log(response);
            if(response.ok){
                console.log("success!!!");
                setDownloadReady(true);
            }else{
                setError(response.error);
            }

        }
        catch(error){
            setError(error);
        };
    }
    return(

        <YoutubeDLContainer>
            <h1>youtube dl</h1>
            <input ref = {textInput} type="text"/>
            <FormatContainer>
                {formatButtons}
            </FormatContainer>
            <button onClick={convertYT}>convert yt</button>
            {error && (<p>{error.toString()}</p>)}
            {downloadReady && <button onClick={downloadVideo}>DOWNLOAD!!</button>}
        </YoutubeDLContainer>


    )
}

function FormatButton({onClick, format, active}){
    return (
        <StyledFormatButton
            $active = {active}
            onClick = {onClick}
        >{format}</StyledFormatButton>
    )
}

const YoutubeDLContainer = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
`
const FormatContainer = styled.div`

    display: flex;
    justify-content: center;

`

const StyledFormatButton = styled.button`
    background-color: ${props => props.$active?"red":"white"};
    padding: 0 1rem;

`
export default YoutubeDL;
